package com.synapticloop.sample.h2zero.sqlite3.impex;

// - - - - thoughtfully generated by synapticloop h2zero - - - - 
//    with the use of synapticloop templar templating language
//                 (/impex/impex-exporter.templar)

import com.synapticloop.h2zero.exception.H2ZeroParseException;
import java.io.BufferedWriter;
import com.synapticloop.sample.h2zero.sqlite3.finder.PetFinder;
import com.synapticloop.sample.h2zero.sqlite3.model.Pet;
import java.util.List;
import java.io.IOException;

public class PetExporter {
	public static void exportToFile(BufferedWriter bufferedWriter) throws IOException {
		List<Pet> all = PetFinder.findAllSilent();
		for (Pet pet: all) {
			bufferedWriter.write(ImpexConverter.convertToString(pet.getIdPet()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(pet.getNmPet()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(pet.getNumAge()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(pet.getFltWeight()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(pet.getDtBirthday()));
			bufferedWriter.write("\n");
		}
	}

}