package synapticloop.sample.h2zero.sqlite3.impex;

// - - - - thoughtfully generated by synapticloop h2zero - - - - 
//    with the use of synapticloop templar templating language
//                 (/impex/impex-exporter.templar)

import synapticloop.h2zero.exception.H2ZeroParseException;
import java.io.BufferedWriter;
import synapticloop.sample.h2zero.sqlite3.finder.AuthorFinder;
import synapticloop.sample.h2zero.sqlite3.model.Author;
import java.util.List;
import java.io.IOException;

public class AuthorExporter {
	public static void exportToFile(BufferedWriter bufferedWriter) throws IOException {
		List<Author> all = AuthorFinder.findAllSilent();
		for (Author author: all) {
			bufferedWriter.write(ImpexConverter.convertToString(author.getIdAuthor()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getIdAuthorStatus()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getTxtIdAuthor()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getNmAuthor()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getNmUsername()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getTxtBio()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getTxtUrlCacheImage()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getNumFollowing()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getNumFollowers()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getDtmStartedFollowing()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getFlIsUpdating()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getFlAuthorIsFollowingUser()));
			bufferedWriter.write("\t");
			bufferedWriter.write(ImpexConverter.convertToString(author.getFlAuthorIsFollowedByUser()));
			bufferedWriter.write("\n");
		}
	}

}